mapper.select:
SELECT id, role, is_deleted, created_at, modified_at, user_id, group_id
FROM group_users
WHERE group_id = #{groupId}
  AND user_id = #{userId}
  AND is_deleted = 'F'

SELECT id, role, is_deleted, created_at, modified_at, user_id, group_id
FROM group_users
WHERE group_id = #{groupId}
  AND user_id = #{userId}

SELECT COUNT(*) > 0
FROM group_users
WHERE group_id = #{groupId}
  AND user_id = #{userId}
  AND role = 'OWNER'
  AND is_deleted = 'F'

SELECT COUNT(*) > 0
FROM group_users
WHERE group_id = #{groupId}
  AND user_id = #{userId}
  AND role IN ('OWNER', 'EDITOR')
  AND is_deleted = 'F'

SELECT COUNT(*) > 0
FROM group_users
WHERE group_id = #{groupId}
  AND user_id = #{userId}
  AND is_deleted = 'F'
