mapper.sql:
WHERE s.is_deleted = 'F'



    AND s.content_type IN

        #{t}





    AND (s.start_time >= #{cond.startDate} OR s.start_time IS NULL)


    AND (s.end_time <= DATE_ADD(#{cond.endDate}, INTERVAL 1 DAY) OR s.end_time IS NULL)






    AND (

        (s.avg_rating >= #{r} AND s.avg_rating < #{r} + 1)

    )




    AND COALESCE(sl.likes_count, 0) >= #{cond.likesCountMin}




    AND (
    s.title      LIKE CONCAT('%', #{cond.searchKeyword}, '%')
    OR s.overview LIKE CONCAT('%', #{cond.searchKeyword}, '%')
    OR s.doro_addr LIKE CONCAT('%', #{cond.searchKeyword}, '%')
    OR s.jibun_addr LIKE CONCAT('%', #{cond.searchKeyword}, '%')
    )




    AND (

        rh.hashtag LIKE CONCAT('%', #{t}, '%')

    )




    AND s.sido_id IN

        #{rid}

mapper.select:
SELECT
GROUP BY s.id, s.title, s.content_type, s.avg_rating, s.start_time, s.end_time, s.doro_addr, s.image1
LIMIT #{limit} OFFSET #{offset}
